import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-scroll';
import { useInView } from 'react-intersection-observer';

const ScrollableImages = () => {
    const [layout1Ref, layout1InView] = useInView();
    const [layout3Ref, layout3InView] = useInView();
    const [layout1Offset, setLayout1Offset] = useState(0);
    const [layout3Offset, setLayout3Offset] = useState(0);
    const layout2Ref = useRef(null);

    useEffect(() => {
        if (layout1InView && layout1Ref.current) {
          setLayout1Offset(Math.max(0, layout1Ref.current.getBoundingClientRect().top));
        }
        if (layout3InView && layout3Ref.current) {
          setLayout3Offset(Math.min(0, layout3Ref.current.getBoundingClientRect().top));
        }
      }, [layout1InView, layout3InView]);

    return (
        <div className="we__img">
            <Link to="layout1" smooth={true} duration={500}>
                Scroll to Layout 1
            </Link>
            <Link to="layout3" smooth={true} duration={500}>
                Scroll to Layout 3
            </Link>
            <div className="we__layout1" ref={layout1Ref} style={{ top: layout1Offset }}>
                <img src="img/layout1.png" alt="" />
            </div>
            <div className="we__layout2" ref={layout2Ref}>
                <img src="img/layout2.png" alt="" />
            </div>
            <div className="we__layout3" ref={layout3Ref} style={{ top: layout3Offset }}>
                <img src="img/layout3.png" alt="" />
            </div>
        </div>
    );
};

export default ScrollableImages;
