import React, { Component } from 'react';

class Checkbox extends Component {
    constructor() {
        super();
        this.state = {
            isChecked: false,
        };
    }

    handleCheckboxChange = () => {
        this.setState((prevState) => ({
            isChecked: !prevState.isChecked,
        }));
    };

    render() {
        const { isChecked } = this.state;

        return (
            <label className={`feedback__form-checkbox ${isChecked ? 'active' : ''}`}>
                <input
                    type="checkbox"
                    name="checkbox"
                    id="checkbox"
                    checked={isChecked}
                    onChange={this.handleCheckboxChange}
                />
                <span className={`feedback__form-span ${isChecked ? 'active' : ''}`} >
                    <svg width="8" height="8" viewBox="0 0 8 8" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <g clip-path="url(#clip0_222_224)">
                            <path d="M6.66659 2L2.99992 5.66667L1.33325 4" stroke="#121621" stroke-width="2" stroke-linecap="square" stroke-linejoin="round" />
                        </g>
                        <defs>
                            <clipPath id="clip0_222_224">
                                <rect width="8" height="8" fill="white" />
                            </clipPath>
                        </defs>
                    </svg>

                </span>
                <p>Я даю своё согласие на обработку персональных данных в соответствии с полным текстом <a href="">Пользовательского соглашения</a> и <a href="">Политики конфиденциальности</a></p>
            </label>
        );
    }
}

export default Checkbox;
