import React, { Component } from 'react';
import { Element, scroller } from 'react-scroll';


class ScrollableImages extends Component {
    constructor(props) {
        super(props);
        this.state = {
            layout1Top: 0, // Начальные значения для стилей
            layout3Top: 500,
        };
    }

    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.handleScroll);
    }

    handleScroll = () => {
        // Определите, какой элемент отображается в области видимости
        // и обновите состояние в соответствии с этим
        const layout1Element = document.querySelector('.we__layout1');
        const layout3Element = document.querySelector('.we__layout3');
        const layout1Rect = layout1Element.getBoundingClientRect();
        const layout3Rect = layout3Element.getBoundingClientRect();

        if (layout1Rect.top <= 0) {
            this.setState({ layout1Top: 0 });
        } else {
            this.setState({ layout1Top: -layout1Rect.top });
        }

        if (layout3Rect.bottom >= window.innerHeight) {
            this.setState({ layout3Top: 0 });
        } else {
            this.setState({ layout3Top: window.innerHeight - layout3Rect.bottom });
        }
    }

    render() {
        return (
            <>
                <img
                    src="img/layout1.png"
                    className="we__layout1"
                    alt=""
                    style={{ top: `${this.state.layout1Top}px` }}
                />
                <img
                    src="img/layout2.png"
                    className="we__layout2"
                    alt=""
                />
                <img
                    src="img/layout3.png"
                    className="we__layout3"
                    alt=""
                    style={{ top: `${this.state.layout3Top}px` }}
                />
            </>
        );
    }
}

export default ScrollableImages;
