import React, { Component } from 'react';

class Checkbox extends Component {
    constructor() {
        super();
        this.state = {
            isChecked: false,
        };
    }

    handleCheckboxChange = () => {
        this.setState((prevState) => ({
            isChecked: !prevState.isChecked,
        }));
    };

    render() {
        const { isChecked } = this.state;

        return (
            <label className={`feedback__form-checkbox ${isChecked ? 'active' : ''}`}>
                <input
                    type="checkbox"
                    name="checkbox"
                    id="checkbox"
                    checked={isChecked}
                    onChange={this.handleCheckboxChange}
                />
                <p>Я даю своё согласие на обработку персональных данных в соответствии с полным текстом <a href="">Пользовательского соглашения</a> и <a href="">Политики конфиденциальности</a></p>
            </label>
        );
    }
}

export default Checkbox;
