import React, { useEffect, useState } from 'react';

function We() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    // Функция обработки скролла
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    // Добавление обработчика события скролла
    window.addEventListener('scroll', handleScroll);

    // Удаление обработчика при размонтировании компонента
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Вычисление вертикального смещения для layout2
  const layout2Top = 20 + scrollY * 0.5;
  const layout3Top = 40 + scrollY;

  return (
    <div className="wrapper we">
      <h2 className="we__title second__title">Кто&nbsp;мы</h2>
      <div className="we__img">
      <img src="img/layout1.png" className="we__layout1" alt="" />
      <img
        src="img/layout2.png"
        className="we__layout2"
        alt=""
        style={{ top: `${layout2Top}%`, zIndex: 2 }}
      />
      <img
        src="img/layout3.png"
        className="we__layout3"
        alt=""
        style={{ top: `${layout3Top}%`, zIndex: 1 }}
      />
    </div>
      <div className="we__descr">
        <p className="strong__text">Венчурная студия ProfitLab —</p>
        <p className="we__descr--main">компания, которая даёт возможности для&nbsp;роста и&nbsp;развития стартапов,
          разрабатывает и&nbsp;обеспечивает их&nbsp;целым рядом сервисов и&nbsp;необходимых ресурсов</p>
      </div>
    </div>
  );
}

export default We;
